const BlogPreview = ({ entry, widgetFor, getAsset }) => {
  const title = entry.getIn(["data", "title"]);
  const body = widgetFor("body");
  const cover = getAsset(entry.getIn(["data", "cover"]));
  return CMS.h("article", {},
    CMS.h("h1", {}, title),
    CMS.h("div", {}, body),
    cover && CMS.h("img", { src: cover.toString() })
  );
};

CMS.registerPreviewTemplate("blog", BlogPreview);


// Inline CSS f√ºr Preview
CMS.registerPreviewStyle(`
  article { 
    font-family: Arial, sans-serif; 
    max-width: 700px; 
    margin: 2rem auto; 
    line-height: 1.6; 
    color: #222; 
  }
  h1 { color: #111; }
  p { color: #333; }
  img { max-width: 100%; height: auto; margin-top: 1rem; }
`, { raw: true });
